mapper.select:
	select
    a.*, sd_type.item_name 'alarm_type', sd_status.item_name 'alarm_status'
    from alarm a, sys_dictionary sd_type, sys_dictionary sd_status
    where user_id = #{userId,jdbcType=INTEGER}
      
        and alarm_id = #{alarmId,jdbcType=INTEGER}
      
      
        and project_name = #{projectName,jdbcType=VARCHAR}
      
      
        and detection_id = #{detectionId,jdbcType=INTEGER}
      
      
        and smu_cms_id = #{smuCmsId,jdbcType=INTEGER}
      
      
        and sensor_id = #{sensorId,jdbcType=INTEGER}
      
      
        and alarm_type_id = #{alarmTypeId,jdbcType=INTEGER}
      
      
        and alarm_context like '%${_parameter.messageContext}%'
      
      
        and (create_time >= #{startCreateTime,jdbcType=TIMESTAMP})
      
      
        and (create_time <= #{endCreateTime,jdbcType=TIMESTAMP})
      
      
        and status = #{status,jdbcType=VARCHAR}
      
      and a.alarm_type_id = sd_type.dic_id
      and a.status = sd_status.dic_id
      order by create_time desc
