mapper.select:
select

FROM threshold 
where project_id = #{0} 
and detection_type_id = #{1}

mapper.insert:
insert into threshold (threshold_id, user_id, project_id, 
  project_type_id, detection_type_id, min_threshold_value, 
  max_threshold_value, heat_image_url, physical_image_url
  )
values (#{thresholdId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{projectId,jdbcType=INTEGER}, 
  #{projectTypeId,jdbcType=INTEGER}, #{detectionTypeId,jdbcType=INTEGER}, #{minThresholdValue,jdbcType=REAL}, 
  #{maxThresholdValue,jdbcType=REAL}, #{heatImageUrl,jdbcType=VARCHAR}, #{physicalImageUrl,jdbcType=VARCHAR}
  )
